module.exports = function ({ settings = {}, variants = ['responsive'] }) {
    return function ({ e, addUtilities }) {

        addUtilities({
            '.center-x': {
                left: '50%',
                transform: 'translateX(-50)',
            },
            '.center-y': {
                left: '50%',
                transform: 'translateX(-50)',
            },
        }, { variants, respectImportant: true });
    };
};
